mapper.select:
select DISTINCT gugunName 
from baseaddress 
where sidoName = #{si}

select DISTINCT dongName 
from baseaddress 
where gugunName = #{gu}

select i.aptCode, i.aptName, i.dongName, i.buildYear, i.lat, i.lng, d.dealAmount, d.dealYear, d.dealMonth, d.dealDay, d.area, d.floor
from houseinfo i, housedeal d 
where i.aptCode = d.aptcode and i.dongName = #{dong}
